    { 
        path:  "/guarded" , 
        component:  "example-view" , 
        guard :  ( )  =>  { 
            // A 50/50 chance to get on page 
            return  Math . random ( )  >=  0.5 
        } 
    } 
] ; 

new  SimplrRouter ( {  routes } ) . init ( ) ; 





